COMMENT ON COLUMN ppdm.rm_info_item_content.land_sale_offering_id
    IS 'LAND SALE OFFERING ID: Unique identifier for the land sale offering.';

COMMENT ON COLUMN ppdm.rm_info_item_content.lithology_log_id
    IS 'LITHOLOGY LOG ID: A unique identifier for a descriptive record of lithology.';

COMMENT ON COLUMN ppdm.rm_info_item_content.lith_log_source
    IS 'LITH LOG SOURCE: The source of data for this lithologic log.';

COMMENT ON COLUMN ppdm.rm_info_item_content.lith_sample_id
    IS 'LITHOLOGIC SAMPLE IDENTIFIER: unique identifier for a lithologic sample, such as rock cutting, mud sample, core etc.';

COMMENT ON COLUMN ppdm.rm_info_item_content.notification_id
