COMMENT ON COLUMN ppdm.rm_info_item_content.organization_seq_no
    IS 'BUSINESS ASSOCIATE ORGANIZATION IDENTIFIER: unique identifier for an organization of a company or organization.';

COMMENT ON COLUMN ppdm.rm_info_item_content.paleo_summary_id
