COMMENT ON COLUMN ppdm.rm_info_item_content.well_license_source
    IS 'WELL LICENSE SOURCE: the source of the well license document.';

COMMENT ON COLUMN ppdm.rm_info_item_content.well_log_curve_id
    IS 'CURVE IDENTIFIER: Unique identification number assigned to the curve or trace.';

COMMENT ON COLUMN ppdm.rm_info_item_content.well_log_id
    IS 'WELL LOG ID: Unique identifier for the well log.';

COMMENT ON COLUMN ppdm.rm_info_item_content.well_log_source
    IS 'WELL LOG SOURCE: The source of this well log data.';

COMMENT ON COLUMN ppdm.rm_info_item_content.wl_dgtz_curve_id
