COMMENT ON COLUMN ppdm.rm_info_item_content.work_order_id
    IS 'WORK ORDER IDENTIFIER: unique identifier for the work order.';

COMMENT ON COLUMN ppdm.rm_info_item_content.wtf_fluid_type
